import React, { useEffect } from 'react';
import { SafeAreaView, StyleSheet, Text, TextInput } from "react-native";
import { Button } from "react-native-paper";
import {
  AdMobBanner,
  AdMobInterstitial,
  PublisherBanner,
  AdMobRewarded,
  setTestDeviceIDAsync,
} from 'expo-ads-admob';

export default function App() {
  const [loading, setLoading] = React.useState(true);
  const [text, setText] = React.useState("");
  const [suggestName, setSuggestName] = React.useState("");
  const [pronounce, setPronounce] = React.useState("");


  const handleClick = () => {
    fetch(
      `http://localhost:8080/female/${text}`,
    )
    .then(res => res.json())
    .then(response => {
      setSuggestName(response[0]);
      setPronounce(response[1]);
      setLoading(false);
    })    
  }

  const goBack = () => {
    setSuggestName("");
    setPronounce("");
    setLoading(true);
  }

  useEffect(() => {

  }, [loading]);


  if (loading) {
    return (
      <SafeAreaView >

        <Text style={styles.titleText}>{"\n"}Suggest Korean Female Nickname</Text>
          <TextInput
          style={styles.inputText}
          onChangeText={input => setText(input)}
          placeholder="Please type your name(lower alphabet only)"
          value={text.replace(/[^a-z]/g, '')}
        />
        <Button
          mode="contained"
          onPress={handleClick}
        >
          Submit
        </Button>
        <Text style={styles.titleText}>
          {suggestName} 
          {pronounce}
        </Text>
        <AdMobBanner
      bannerSize="fullBanner"
      adUnitID="ca-app-pub-4002786977128549/3343757792" // Test ID, Replace with your-admob-unit-id
      servePersonalizedAds // true or false
      />
      </SafeAreaView>

    );
  }else{
    return (
      <SafeAreaView >

        <Text style={styles.titleText}>{"\n"} Suggest Korean Female Nickname</Text>
        <Text style={styles.titleText}>
          {suggestName} 
          {pronounce}
        </Text>
        <Button 
          mode="contained"
          onPress={goBack}
        >
          Go Back
        </Button>
      </SafeAreaView>
    )
  }

}

const styles = StyleSheet.create({
  baseText: {
    fontFamily: "Cochin"
  },
  titleText: {
    textAlign: 'center',
    fontSize: 20
  },
  inputText: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
