import React, { useEffect } from 'react';
import { SafeAreaView, StyleSheet, Text, TextInput } from "react-native";
import { Button } from "react-native-paper";
// import {useHistory} from "react-router-dom";

export default function App() {
  const [loading, setLoading] = React.useState(true);
  const [text, setText] = React.useState("");
  const [suggestName, setSuggestName] = React.useState("");
  const [pronounce, setPronounce] = React.useState("");

  // const history = useHistory();


  const handleClick = () => {
    fetch(
      `http://localhost:8080/female/${text}`,
    )
    // .then(res => console.log(res))
    .then(res => res.json())
    // .then(response => console.log(response))
    .then(response => {
      setSuggestName(response[0]);
      setPronounce(response[1]);
      setLoading(false);
    })    
  }

  const goBack = () => {
    setSuggestName("");
    setPronounce("");
    setLoading(true);
  }

  useEffect(() => {

  }, [loading]);


  if (loading) {
    return (
      <SafeAreaView >
        <br/>
        <Text style={styles.titleText}>Suggest Korean Nickname</Text>
          <br/><br/><br/>
          <TextInput
          style={styles.inputText}
          onChangeText={input => setText(input)}
          // onChangeText={onChangeText}
          placeholder="Please type your Alphabet(lower) name"
          value={text.replace(/[^a-z]/g, '')}
        />
        <Button
          mode="contained"
          onPress={handleClick}
          // onPress={() => console.log({text})}
        >
          Submit
        </Button>
        <Text style={styles.titleText}>
          {suggestName} 
          {pronounce}
        </Text>
      </SafeAreaView>
    );
  }else{
    return (
      <SafeAreaView >
        <br/>
        <Text style={styles.titleText}>Suggest Korean Nickname</Text>
        <br/><br/><br/>
        <Text style={styles.titleText}>
          {suggestName} 
          {pronounce}
        </Text>
        <Button 
          mode="contained"
          onPress={goBack}
        >
          Go Back
        </Button>
      </SafeAreaView>
    )
  }

}

const styles = StyleSheet.create({
  baseText: {
    fontFamily: "Cochin"
  },
  titleText: {
    textAlign: 'center',
    fontSize: 20
  },
  inputText: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
