import React, { Component } from 'react';
import { SafeAreaView, StyleSheet, Text, TextInput } from "react-native";
import { Button } from "react-native-paper";

export default function App() {
  const [text, setText] = React.useState("");
  const [suggestName, setSuggestName] = React.useState("initial");

  handleClick = async () => {

      try {
        let text = {text}
        let response = await fetch(
          "locahost:8080/female/"+text,
        );
        let responseJson = await response.json();
        console.log(responseJSon);
        return responseJson;
      } catch (error) {
        console.error(error);
      }
  }
  return (
    <SafeAreaView >
      <Text style={styles.titleText}>Suggest Korean Nickname</Text>
      <TextInput
        style={styles.inputText}
        onChangeText={text => setText(text)}
        // onChangeText={onChangeText}
        placeholder="Please type your Alphabet name"
        value={text}
      />
      <Button
        mode="contained"
        onPress={this.handleClick}
        // onPress={() => console.log({text})}
      >
        Submit
      </Button>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  baseText: {
    fontFamily: "Cochin"
  },
  titleText: {
    textAlign: 'center',
    fontSize: 20
  },
  inputText: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
