import React, { useEffect } from 'react';
import { SafeAreaView, StyleSheet, Text, TextInput } from "react-native";
import { Button } from "react-native-paper";

export default function App() {
  const [input, setInput] = React.useState("");
  const [text, setText] = React.useState("");
  const [suggestName, setSuggestName] = React.useState("initial");


  handleClick = () => {
    setText(input);
  }

  useEffect(() => {
    fetch(
      `http://locahost:8080/female/${text}`,
    )
    .then(res => res.json())
    .then(response => {
      setSuggestName(response[0])
    })
  }, [suggestName])

  return (
    <SafeAreaView >
      <Text style={styles.titleText}>Suggest Korean Nickname</Text>
      <TextInput
        style={styles.inputText}
        onChangeText={input => setText(input)}
        // onChangeText={onChangeText}
        placeholder="Please type your Alphabet name"
        value={text}
      />
      <Button
        mode="contained"
        onPress={handleClick}
        // onPress={() => console.log({text})}
      >
        Submit
      </Button>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  baseText: {
    fontFamily: "Cochin"
  },
  titleText: {
    textAlign: 'center',
    fontSize: 20
  },
  inputText: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
